-- --------------------------------------------------------

--
-- 表的结构 `yxt_section`
-- 课时管理
--

CREATE TABLE IF NOT EXISTS `yxt_section` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `cs_id` int(6) DEFAULT NULL,
  `sc_name` varchar(50) DEFAULT NULL,
  `is_free` int(2) NOT NULL DEFAULT '0',
  `sc_time` varchar(16) DEFAULT NULL,
  `playtimes` int(2) DEFAULT NULL,
  `yun_url` varchar(600) DEFAULT NULL,
  `videoid` int(15) NOT NULL,
  `doccontent` longtext,
  `addtime` datetime DEFAULT NULL,
  `state` int(2) DEFAULT NULL,
  `listorder` int(10) DEFAULT NULL,
  `type_id` int(2) NOT NULL COMMENT '"1"为章，"0"为节',
  `zhang_id` int(2) NOT NULL DEFAULT '0',
  `video_type` int(2) DEFAULT NULL COMMENT '"1"为云存储，"2"为优酷视频地址',
  `section_type` int(2) DEFAULT NULL COMMENT '"1"为视频课程，"2"为doc课程',
  `playpass` varchar(10) DEFAULT NULL COMMENT '优酷的播放密码',
  `live_starttime` datetime DEFAULT NULL COMMENT '直播开始时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=22 ;

--
-- 转存表中的数据 `yxt_section`
--

INSERT INTO `yxt_section` (`id`, `cs_id`, `sc_name`, `is_free`, `sc_time`, `playtimes`, `yun_url`, `videoid`, `doccontent`, `addtime`, `state`, `listorder`, `type_id`, `zhang_id`, `video_type`, `section_type`, `playpass`, `live_starttime`) VALUES
(1, 1, '如何使用云存储', 0, '', 0, '', 0, '<p>云存储是用来储存视频课程文件和文档（比如课件ppt等）文件的。易学堂集成了腾讯云COS以及万视无忧，在后台可以自由切换。</p><p>下面就来说说如何使用<br/></p><p>一、腾讯云COS</p><p>1、打开腾讯云官网：<a href="https://www.qcloud.com/">https://www.qcloud.com/</a></p><p>2、注册账号，登录。（用QQ号登录，没有QQ号的去注册一个），初次登录要进行邮箱验证。</p><p>3、登录后，点击管理中心--&gt;云产品</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476523013797771.png" title="1476523013797771.png" alt="QQ截图20161015172542.png" width="1001" height="377"/></p><p>找到“对象存储服务”</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476523136655600.png" title="1476523136655600.png" alt="QQ截图20161015172745.png" width="1006" height="534"/></p><p>初次只用要进行实名认证，根据提示请自行完成认证。</p><p>4、认证完成后，登录账号，创建Bucket。（可以理解为文件夹）</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476523388954105.png" title="1476523388954105.png" alt="QQ截图20161015173107.png" width="1008" height="331"/></p><p>5、获取API秘钥</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476523647132332.png" title="1476523647132332.png" alt="QQ截图20161015173650.png" width="1004" height="460"/></p><p>6，把秘钥和上面建好的Bucket填写到网站后台配置中。</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476523797103883.png" title="1476523797103883.png" alt="QQ截图20161015173903.png" width="1002" height="445"/></p><p>二、万视无忧</p><p>1、打开万视无忧的网站：<a href="http://www.wsview.com/">http://www.wsview.com/</a></p><p>2、注册登录账号</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476524012605918.png" title="1476524012605918.png" alt="QQ截图20161015174240.png" width="1001" height="426"/></p><p>3、把注册的账号和密码填写到网站配置中</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476524188426214.png" title="1476524188426214.png" alt="QQ截图20161015174545.png" width="997" height="343"/></p><p>三、两者比较</p><p>1、从注册，配置上看，万视无忧比腾讯云简单，万视无忧只需要账号和密码即可。</p><p>2、从价格上看,万视无忧比腾讯云更实惠一些。好在两者都有免费额度。</p><p>3、从品牌上看，腾讯大于万视无忧。</p><p>4、关于易学堂CMF,因为腾讯COS的API需要集成到源代码中，上传时会消耗本地服务器资源，而且上传文件大小限制需要配置服务器，新手不易操作，设置不好会限制上传文件大小。</p><p>而万视无忧直接使用万视无忧服务器的API，无需配置，上传文件大小基本无限制。</p><p>4、建议用万视无忧，因为简单方便，</p><p><br/></p>', '2016-11-09 19:33:21', 1, 0, 0, 0, 0, 2, '', '0000-00-00 00:00:00'),
(2, 1, '如何配置云直播功能', 0, '', 0, '', 0, '<p>易学堂集成了腾讯云直播和万视无忧云直播，下面就来说说这两者的配置</p><p>一、腾讯云直播</p><p>1、登录腾讯云，<a href="https://www.qcloud.com/">https://www.qcloud.com</a>（没有账号的自行注册，激活，认证）</p><p>2、主页--云产品--是睥睨服务--直播</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476526777328096.png" title="1476526777328096.png" alt="QQ截图20161015181313.png" width="987" height="465"/></p><p>3、点击立即选购</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476526899131636.png" title="1476526899131636.png" alt="QQ截图20161015181433.png" width="988" height="390"/></p><p>4、初次使用需要申请开通，申请免费，申请后最好写个工单，这样审核会快一点。</p><p>5、审核通过后的界面是这样的：</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476527012743697.png" title="1476527012743697.png" alt="QQ截图20161015181556.png" width="990" height="377"/></p><p>6、上图箭头指向的appid请记住，后台配置时会用到。</p><p>7、获取云API秘钥，云产品--监控与管理---云API---立即使用</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476527336817608.png" title="1476527336817608.png" alt="QQ截图20161015181920.png" width="996" height="436"/></p><p>8、获取秘钥，要是没有，点击新建秘钥</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476527400879416.png" title="1476527400879416.png" alt="QQ截图20161015182117.png" width="998" height="312"/></p><p>9、打开网站后台，填写上面得到的appid和秘钥（secretid secretkey）</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476527552102967.png" title="1476527552102967.png" alt="QQ截图20161015184152.png" width="999" height="370"/></p><p>二、万视无忧云直播</p><p>1、打开万视无忧网站：<a href="http://www.wsview.com/">http://www.wsview.com</a></p><p>2、注册登录账号。</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476527772110380.png" title="1476527772110380.png" alt="QQ截图20161015184525.png" width="1002" height="486"/></p><p>3、把注册的账号和密码填写到网站后台配置中。</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476527858138945.png" title="1476527858138945.png" alt="QQ截图20161015184652.png" width="998" height="353"/></p><p>4、注：点击上面的按钮可以腾讯直播和万视无忧直播间切换，前台自动识别。</p><p><br/></p>', '2016-11-09 19:33:55', 1, 0, 0, 0, 0, 2, '', '0000-00-00 00:00:00'),
(3, 1, '如何配置短信验证码', 0, '', 0, '', 0, '<p>易学堂CMF使用的是阿里大于的短信平台，下面说说如何配置使用<br/></p><p>1、登录阿里大于的网站：<a href="http://www.alidayu.com/">http://www.alidayu.com/</a></p><p>2，注册，登录（账号用淘宝账号就行，一家的），初次使用送20元。</p><p>3、登录后进入管理中心</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476528236859964.png" title="1476528236859964.png" alt="QQ截图20161015185301.png" width="957" height="405"/></p><p>4、应用管理--创建应用。</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476528381913275.png" title="1476528381913275.png" alt="QQ截图20161015185501.png" width="958" height="381"/></p><p>5、创建后记住appkey,后台配置中需要。</p><p>6、创建签名：配置管理--验证码---配置短信签名--添加签名（需审核，不过很快，半小时差不多）<br/></p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476528505121095.png" title="1476528505121095.png" alt="QQ截图20161015185721.png" width="969" height="403"/></p><p>7、配置短信模板：：配置管理--验证码--配置短信模板--添加模板</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476528808878650.png" title="1476528808878650.png" alt="QQ截图20161015190242.png" width="993" height="459"/></p><p>模板内容中的验证码变量一定是${number},不然会影响短信的发送。</p><p>8、将信息添加到后台配置中：</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161015/1476528988574189.png" title="1476528988574189.png" alt="QQ截图20161015190550.png" width="998" height="498"/></p><p><br/></p>', '2016-11-09 19:34:26', 1, 0, 0, 0, 0, 2, '', '0000-00-00 00:00:00'),
(4, 2, '如何申请成为教师', 0, '', 0, '', 0, '<p>1、注册，登陆账户。</p><p>2、首页右侧，点击姓名--成为教师</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161017/1476694608111963.png" title="1476694608111963.png" alt="QQ截图20161017165610.png" width="984" height="391"/></p><p>3、填写姓名<img id="loading_iudu044f" src="http://teach.yxtcmf.com/public/js/ueditor/themes/default/images/spacer.gif" title="正在上传..."/>和上传教师资格证。两者姓名要一致。</p><p><br/></p>', '2016-11-09 19:36:44', 1, 0, 0, 0, 0, 2, '', '0000-00-00 00:00:00'),
(5, 2, '如何发布视频课程', 0, '', 0, '', 0, '<p>1、用教师账号登陆系统。</p><p>2、我 的教室，创建课程</p><p><img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161018/1476776941706575.png" alt="1476776941706575.png"/></p><p>3、<img src="http://teach.yxtcmf.com/ueditor/php/upload/image/20161018/1476777270666503.png" title="1476777270666503.png" alt="QQ截图20161018155501.png"/></p><p>4、点击创建，填写课程信息即可。</p><p><br/></p>', '2016-11-09 19:37:09', 1, 0, 0, 0, 0, 2, '', '0000-00-00 00:00:00'),
